INSERT INTO `products` (`id`, `title`, `description`, `author`, `price`, `quantity`) VALUES
(1, 'Принципы', 'Жизненные и управленческие принципы Рэя Далио, одного из самых влиятельных и богатых людей планеты, и история его детища - крупнейшего в мире хедж-фонда Bridgewater.Рэй Далио вырос в обычной семье со средним достатком. Основанная им в 26 лет инвестиционная фирма Bridgewater Associates в течение следующих 40 лет стала пятой по важности частной компанией в США (по версии Fortune). Секрет ее устойчивости и успеха в том, что Рэй Далио в своей жизни и работе придерживается универсальных принципов, которые выкристаллизовались из ряда побед и поражений. Сила этих личных принципов такова, что они изменили всю отрасль: журнал CIO даже назвал Далио «Стивом Джобсом инвестирования».', 'Александр Неский', '1084.00', 10),
(2, 'Джедайские техники', 'Практичные, проверенные и обоснованные приемы, которые помогут очистить папку \"Входящие\", сберечь самый важный ресурс - мыслетопливо, сделать список задач реально работающим и гарантированно добиваться результатов. Несмотря на то, что мы знаем, как надо правильно работать, у многих из нас все равно есть с этим огромная беда!', 'Пидро Пидроссо', '523.00', 5),
(3, 'Революция', 'Олег Тиньков - уникальный предприниматель, доказавший, что успешный бизнес можно создать в разных отраслях. Он построил такие компании как \"Техношок\", \"Дарья\", завод и сеть ресторанов \"Тинькофф\", а после успешной продажи пивного бизнеса решил вложить капитал в инновационный банк.', 'Васильг Харикейн', '478.00', 15),
(4, 'Красная таблетка', '\"Красная таблетка. Посмотри правде в глаза!\"Книга о том, как мозг нас обманывает, и как с ним договориться.', 'Иван Васильев', '396.00', 7),
(5, 'Холодная кровь', 'На южном берегу Темзы обнаруживают большой чемодан, в котором лежит обезображенный обезглавленный мужчина. Дело поручают старшему инспектору Эрике Фостер и ее напарнице Мосс.', 'Фак Факинг', '219.00', 25),
(6, 'Швейцарец', 'Семь лет назад русский эмигрант Алекс Штрауб, перебравшийся в Европу, нашел портал в прошлое и оказался в 1921 году. Решив обыграть эту странную ситуацию в свою пользу, Алекс немного поменял ход истории. Вернувшись в свое время, он обнаружил: России больше не существует, поскольку нацисты выиграли Вторую мировую войну.', 'Пончик Хуёнчик', '199.00', 25),
(7, 'Черная Весна', 'Не всегда возвращение домой может стать праздником, и в этом на собственном примере убедились ученики мага по имени «Ворон». После тягот минувшей войны они рассчитывали немного отдохнуть в солнечной Силистрии, в гостях у своего друга Гарольда Монброна, а вместо этого попали в очень и очень неприятную историю, из которой затруднительно будет выбраться живым.', 'Кончита Педритта', '150.00', 20),
(8, 'На подъеме', 'Эту повесть Стивен Кинг написал к Хеллоуину. Скотт Кэри – разведенный компьютерный дизайнер. В его жизни все складывается не самым лучшим образом: от отношений с соседями до проблем со здоровьем, причину которых Скотт не может объяснить.', 'Стивен Кинг', '239.00', 0),
(9, 'Диетология', 'В новом, пятом, издании руководства приведены основополагающие принципы современной клинической диетологии в сочетании с изложением клинических особенностей течения заболеваний и патологических процессов.', 'Кучка авторов', '1512.00', 2),
(10, 'Свод знаний', 'Управление бизнес-процессами (BPM) – это концепция управления, рассматривающая деятельность организаций через призму процессов (или административных регламентов в случае органов государственного и муниципального управления).', 'Кучка авторов', '999.00', 4);

